import React, { useState } from 'react';
import './Login.css';

const Login = () => {
  const [activeTab, setActiveTab] = useState('register');
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    agree: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`${activeTab} submitted:`, formData);
    // Add your backend integration here
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="logo">
          <div className="logo-circle">B</div>
        </div>

        <h1>Welcome to Bookd</h1>
        <p className="subtitle">Login or create an account to manage your scheduling journey</p>

        <div className="tabs">
          <button
            className={`tab ${activeTab === 'login' ? 'active' : ''}`}
            onClick={() => setActiveTab('login')}
          >
            Login
          </button>
          <button
            className={`tab ${activeTab === 'register' ? 'active' : ''}`}
            onClick={() => setActiveTab('register')}
          >
            Register
          </button>
        </div>

        <form className="login-form" onSubmit={handleSubmit}>
          {activeTab === 'register' && (
            <div className="input-group">
              <span className="input-icon">üë§</span>
              <input
                type="text"
                name="fullName"
                placeholder="Full Name"
                value={formData.fullName}
                onChange={handleChange}
                required
              />
            </div>
          )}

          <div className="input-group">
            <span className="input-icon">‚úâÔ∏è</span>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <span className="input-icon">üîí</span>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          {activeTab === 'register' && (
            <label className="checkbox-container">
              <input
                type="checkbox"
                name="agree"
                checked={formData.agree}
                onChange={handleChange}
                required
              />
              <span className="checkmark"></span>
              I agree to the <a href="#terms">terms and conditions</a>
            </label>
          )}

          <button type="submit" className="login-button">
            {activeTab === 'login' ? (
              <>
                <span className="button-icon">üîì</span> Login
              </>
            ) : (
              <>
                <span className="button-icon">üìù</span> Create account
              </>
            )}
          </button>
        </form>

        <p className="footer">
          By using Bookd, you agree to our <a href="#terms">Terms of Service</a> and <a href="#privacy">Privacy Policy</a>.
        </p>

        <div className="tags">
          <span className="tag-item">üîí Secure login</span>
          <span className="tag-item">üõ°Ô∏è Data privacy</span>
          <span className="tag-item">‚è∞ Personalized scheduling</span>
        </div>
      </div>
    </div>
  );
};

export default Login;
