// import React from 'react';

// const Home = () => {
//   return (
//     <div>
//       <h1>Welcome to Healthcare Appointment System</h1>
//     </div>
//   );
// };

// export default Home;


import React from 'react';

const Home = () => {
  return (
    <div className="bg-white min-h-screen font-sans">
      {/* Banner Section */}
      <div className="text-center py-10 px-4 bg-blue-50">
        <h1 className="text-4xl font-bold text-gray-800">
          Let's find you the <span className="text-blue-600">Best Insurance</span>
        </h1>
        <div className="mt-4 flex justify-center gap-8 text-gray-600">
          <div>
            <span className="text-blue-500">✔</span> 51 insurers offering lowest prices
          </div>
          <div>
            <span className="text-orange-500">⚡</span> Quick, easy & hassle free
          </div>
        </div>
        {/* Hero Image / Banner */}
        <div className="mt-10 flex justify-center">
          <img
            src="/assets/insurance-banner.png" // Replace with actual path
            alt="Insurance Banner"
            className="w-full max-w-2xl rounded-xl shadow-lg"
          />
        </div>
      </div>

      {/* Insurance Categories */}
      <div className="py-12 px-4 grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-6 max-w-6xl mx-auto">
        {insuranceCategories.map((item, index) => (
          <div
            key={index}
            className="border rounded-xl shadow hover:shadow-md transition p-4 text-center"
          >
            <img
              src={item.icon}
              alt={item.title}
              className="w-12 h-12 mx-auto mb-2"
            />
            <h3 className="text-sm font-medium text-gray-700">{item.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

const insuranceCategories = [
  { title: 'Term Life Insurance', icon: '/assets/icons/umbrella.png' },
  { title: 'Health Insurance', icon: '/assets/icons/heart.png' },
  { title: 'Investment Plans', icon: '/assets/icons/coin.png' },
  { title: 'Car Insurance', icon: '/assets/icons/car.png' },
  { title: '2 Wheeler Insurance', icon: '/assets/icons/bike.png' },
  { title: 'Family Health Insurance', icon: '/assets/icons/family.png' },
  { title: 'Travel Insurance', icon: '/assets/icons/plane.png' },
  { title: 'Term Insurance (Women)', icon: '/assets/icons/woman.png' },
  { title: 'Return of Premium', icon: '/assets/icons/return.png' },
  { title: 'Guaranteed Return Plans', icon: '/assets/icons/guarantee.png' },
  { title: 'Child Savings Plans', icon: '/assets/icons/child.png' },
  { title: 'Retirement Plans', icon: '/assets/icons/retire.png' },
];

export default Home;
