import React, { useState } from 'react';
import { ethers } from 'ethers'; // ‚úÖ All you need
import AuthABI from '../contracts/AuthContract.json';
import './Login.css';
import { generateHash } from '../utils/hash'; // ‚úÖ Your hash function

const CONTRACT_ADDRESS = '0xe7f1725E7734CE288F8367e1Bb143E90bb3F0512'; // ‚úÖ Ensure this matches your deployed contract

function Login() {
  const [activeTab, setActiveTab] = useState('login');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const hash = await generateHash(email, password);
    setLoading(true);
    setMessage('');

    try {
      if (!window.ethereum) {
        throw new Error('MetaMask is not installed!');
      }

      await window.ethereum.request({ method: 'eth_requestAccounts' });

      const provider = new ethers.BrowserProvider(window.ethereum); // ‚úÖ MetaMask provider
      const signer = await provider.getSigner(); // ‚úÖ Get signer from MetaMask

      const contract = new ethers.Contract(CONTRACT_ADDRESS, AuthABI.abi, signer);

      if (activeTab === 'register') {
        const tx = await contract.register(email, hash);
        await tx.wait();
        setMessage('‚úÖ Successfully registered on the blockchain!');
      } else if (activeTab === 'login') {
        const loginSuccess = await contract.login(email, hash);
        if (loginSuccess) {
          setMessage('‚úÖ Login successful!');
        } else {
          setMessage('‚ùå Invalid email or password!');
        }
      }
    } catch (error) {
      console.error(error);
      setMessage('‚ùó Error interacting with the contract. Check MetaMask & console.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-wrapper">
      <div className="login-container">
        <div className="logo-circle">PB</div>
        <h1 className="brand-name">policybazaar</h1>

        <div className="welcome-text">
          <h2>Welcome to policybazaar</h2>
          <p>Login or create an account to manage your scheduling journey</p>
        </div>

        <div className="tabs">
          <button
            className={`tab ${activeTab === 'login' ? 'active' : ''}`}
            onClick={() => setActiveTab('login')}
          >
            Login
          </button>
          <button
            className={`tab ${activeTab === 'register' ? 'active' : ''}`}
            onClick={() => setActiveTab('register')}
          >
            Register
          </button>
        </div>

        <form onSubmit={handleSubmit} className="form">
          <div className="input-group">
            <span className="input-icon">üìß</span>
            <input
              type="email"
              placeholder="Email"
              value={email}
              required
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="input-group">
            <span className="input-icon">üîí</span>
            <input
              type="password"
              placeholder="Password"
              value={password}
              required
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input type="checkbox" /> I agree to the terms and conditions
            </label>
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            <span className="button-icon">üîì</span>
            {loading ? 'Processing...' : activeTab === 'login' ? 'Login' : 'Create account'}
          </button>
        </form>

        {message && (
          <p
            style={{
              textAlign: 'center',
              color: message.includes('‚úÖ') ? 'green' : 'red',
              marginTop: '10px',
            }}
          >
            {message}
          </p>
        )}

        <div className="footer">
          <p>
            By using policybazaar, you agree to our{' '}
            <a href="#terms">Terms of Service</a> and{' '}
            <a href="#privacy">Privacy Policy</a>.
          </p>
        </div>

        <div className="tags">
          <span className="tag">Secure login</span>
          <span className="tag">Data privacy</span>
          <span className="tag">Personalized scheduling</span>
        </div>
      </div>
    </div>
  );
}

export default Login;
